mapper.select:
select a.id, a.name, u1.name as owner, a.start_date, a.end_date, a.cost, a.description, a.create_time, u2.name as create_by, a.edit_time, u3.name as edit_by from tbl_activity a inner join tbl_user u1 on a.owner = u1.id inner join tbl_user u2 on a.create_by = u2.id left join tbl_user u3 on a.edit_by = u3.id
and a.name like concat("%",#{name},"%")
and a.owner like concat("%",#{owner},"%")
and a.start_date >= #{start_date}
and a.end_date <= #{end_date}
order by a.create_time desc limit #{pageNo}, #{pageSize}

select count(*) from tbl_activity a inner join tbl_user u1 on a.owner = u1.id inner join tbl_user u2 on a.create_by = u2.id left join tbl_user u3 on a.edit_by = u3.id
and a.name like concat("%",#{name},"%")
and a.owner like concat("%",#{owner},"%")
and a.start_date >= #{start_date}
and a.end_date <= #{end_date}
